/* eslint-disable max-len */
var env = process.env;
var ADBLOCK = is(env.ADBLOCK);
var CI = is(env.CI);
var COLOR = is(env.npm_config_color);
var SILENT = !!~['silent', 'error', 'warn'].indexOf(env.npm_config_loglevel);

function is(it) {
  return !!it && it !== '0' && it !== 'false';
}

function log(it) {
  // eslint-disable-next-line no-console,no-control-regex
  console.log(COLOR ? it : it.replace(/\u001B\[\d+m/g, ''));
}

if (!ADBLOCK && !CI && !SILENT) {
  log('\x1B[96mThank you for using core-js (\x1B[94m https://github.com/zloirock/core-js \x1B[96m) for polyfilling JavaScript standard library!\x1B[0m\n');
  log('\x1B[96mThe project needs your help! Please consider supporting of core-js on Open Collective or Patreon: \x1B[0m');
  log('\x1B[96m>\x1B[94m https://opencollective.com/core-js \x1B[0m');
  log('\x1B[96m>\x1B[94m https://www.patreon.com/zloirock \x1B[0m\n');
  log('\x1B[96mAlso, the author of core-js (\x1B[94m https://github.com/zloirock \x1B[96m) is looking for a good job -)\x1B[0m\n');
}